--  DDL for View COURSEENROLLMENTVIEW
--------------------------------------------------------

  CREATE OR REPLACE FORCE EDITIONABLE VIEW "C##UNIDIDATTICA"."COURSEENROLLMENTVIEW" ("DEGREEPROGRAMNAME", "COURSECODE", "COURSETITLE", "CREDITS", "NUMBEROFENROLLMENTS") AS 
  SELECT 
    DEREF(c.DegreeProgram).DegreeName AS DegreeProgramName,  
    c.CourseCode,                                             
    c.CourseTitle,                                            
    c.Credits,                                                
    COUNT(e.Student) AS NumberOfEnrollments                   
FROM 
    Course c
LEFT JOIN 
    Enrollment e
    ON e.Course = REF(c)                                      
GROUP BY 
    DEREF(c.DegreeProgram).DegreeName,                        
    c.CourseCode,                                             
    c.CourseTitle,                                           
    c.Credits                                               
ORDER BY 
    DEREF(c.DegreeProgram).DegreeName,                      
    c.CourseCode
;
  GRANT SELECT ON "C##UNIDIDATTICA"."COURSEENROLLMENTVIEW" TO "C##GESTIONESTUDENTI";
