  CREATE OR REPLACE FORCE EDITIONABLE VIEW "C##UNIDIDATTICA"."PRINTNOTACTIVESTUDENTVIEW" ("MATRICULATION", "FIRSTNAME", "LASTNAME", "COURSECODE", "COURSETITLE") AS 
  SELECT 
    deref(e.student).matriculation AS Matriculation,
    deref(e.student).firstname AS FirstName,
    deref(e.student).lastname AS LastName,
    deref(e.course).CourseCode AS CourseCode,
    deref(e.course).CourseTitle AS CourseTitle
FROM 
    Enrollment e
WHERE 
    NOT EXISTS (
        SELECT 1 
        FROM Exam ex
        WHERE ex.enroll = REF(e)
    )
    AND NOT EXISTS (
        SELECT 1 
        FROM Assignment a
