  CREATE OR REPLACE EDITIONABLE TRIGGER "C##UNIDIDATTICA"."CHECKPREREQUISITESDEGREEPROGRAM" 
BEFORE INSERT ON Course
FOR EACH ROW
DECLARE
    v_prerequisite CourseType;         -- Variabile per memorizzare il corso prerequisito dereferenziato
    v_degree_program DegreeProgramType; -- Il DegreeProgram del corso prerequisito
    v_new_degree_program DegreeProgramType; -- Il DegreeProgram del nuovo corso che si sta inserendo
BEGIN
    -- Ottiene il DegreeProgram del nuovo corso
    SELECT DEREF(:NEW.DegreeProgram) INTO v_new_degree_program FROM DUAL;

    -- Se l'array dei prerequisiti non è vuoto, controlla ciascun prerequisito
    IF :NEW.Prerequisites IS NOT NULL THEN
        FOR i IN 1..:NEW.Prerequisites.COUNT LOOP
            -- Dereferenzia il corso prerequisito
            SELECT DEREF(:NEW.Prerequisites(i)) INTO v_prerequisite FROM DUAL;

            -- Ottiene il DegreeProgram del prerequisito
            SELECT DEREF(v_prerequisite.DegreeProgram) INTO v_degree_program FROM DUAL;

            -- Se il DegreeProgram del prerequisito è diverso da quello del nuovo corso, solleva un'eccezione
            IF v_degree_program.degreeName != v_new_degree_program.degreeName THEN
                RAISE_APPLICATION_ERROR(-20001, 'Il corso prerequisito ha un DegreeProgram diverso dal corso inserito.');
            END IF;
        END LOOP;
    END IF;
END;
