--  DDL for Procedure CHECKUNPAIDTUITION
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "C##UNIDIDATTICA"."CHECKUNPAIDTUITION" (p_matriculation CHAR) IS
    
    CURSOR unpaid_tuition_cur IS
        SELECT t.amount, t.expiration
        FROM Tuition t
        WHERE DEREF(t.student).matriculation = p_matriculation
          AND t.paid = 'f';

    v_amount Tuition.amount%TYPE;
    v_expiration Tuition.expiration%TYPE;

    v_student_exists CHAR(1);

BEGIN

    SELECT COUNT(*)
    INTO v_student_exists
    FROM Student s
    WHERE s.matriculation = p_matriculation;


    IF v_student_exists = 0 THEN
        dbms_output.put_line('Warning: No student found with matriculation ' || p_matriculation);
    ELSE

        OPEN unpaid_tuition_cur;


        LOOP
            FETCH unpaid_tuition_cur INTO v_amount, v_expiration;
            EXIT WHEN unpaid_tuition_cur%NOTFOUND;

            dbms_output.put_line('Unpaid Amount: ' || v_amount || ', Expiration Date: ' || v_expiration);
        END LOOP;


        CLOSE unpaid_tuition_cur;
    END IF;
END;

/

  GRANT EXECUTE ON "C##UNIDIDATTICA"."CHECKUNPAIDTUITION" TO "C##GESTIONESTUDENTI";
--------------------------------------------------------
--  DDL for Procedure CREATEEXAMFORENROLLMENT
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "C##UNIDIDATTICA"."CREATEEXAMFORENROLLMENT" (
    studentCode CHAR,
    courseCode CHAR
) AS
    enrollmentRef REF EnrollmentType;
    totalAssignments NUMBER := 0;
    sumMarks NUMBER := 0;
    avgMark NUMBER := 0;
BEGIN
    -- Trova l'enrollment corrispondente allo studente e al corso
    SELECT REF(e)
    INTO enrollmentRef
    FROM Enrollment e
    JOIN Course c ON e.Course = REF(c)
    JOIN Student s ON e.Student = REF(s)
    WHERE s.Matriculation = studentCode
      AND c.CourseCode = courseCode;

    -- Calcola il numero totale di assignment e la somma dei voti associati a tale enrollment
    SELECT COUNT(a.Mark), SUM(a.Mark)
    INTO totalAssignments, sumMarks
    FROM Assignment a
    WHERE a.Enroll = enrollmentRef;

    -- Se ci sono assignment, calcola la media arrotondata per eccesso
    IF totalAssignments > 0 THEN
        avgMark := CEIL(sumMarks / totalAssignments);  -- Arrotonda per eccesso
    ELSE
        avgMark := 0;  -- Se non ci sono assignment, assegna 0
    END IF;

    -- Inserisci una nuova riga nella tabella Exam con la media e la data attuale
    INSERT INTO Exam
    VALUES (ExamType(
        enrollmentRef,
        SYSDATE,  -- Data corrente
        avgMark   -- Media dei voti arrotondata
    ));

    -- Stampa un messaggio di conferma (opzionale)
    DBMS_OUTPUT.PUT_LINE('Nuova riga inserita nella tabella Exam con media: ' || avgMark);
END;

/

  GRANT EXECUTE ON "C##UNIDIDATTICA"."CREATEEXAMFORENROLLMENT" TO "C##GESTIONESTUDENTI";
