--------------------------------------------------------
--  DDL for Function CHECKPREREQUISITEDEGREEFUNCTION
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "C##UNIDIDATTICA"."CHECKPREREQUISITEDEGREEFUNCTION" (
    CourseArray CourseArrayType,
    DegreeProgramRef REF DegreeProgramType
) RETURN CHAR
IS
    v_course REF CourseType;           -- Variabile per memorizzare il REF del corso corrente
    v_course_obj CourseType;           -- Variabile per memorizzare il corso de-referenziato
    v_degree_program REF DegreeProgramType; -- Variabile per memorizzare il DegreeProgram del corso
BEGIN
    -- Se l'array di corsi è vuoto, restituisce 't'
    IF CourseArray.COUNT = 0 THEN
        RETURN 't';
    END IF;

    -- Ciclo attraverso l'array di corsi
    FOR i IN 1..CourseArray.COUNT LOOP
        v_course := CourseArray(i);  -- Ottiene il REF del corso corrente

        -- De-referenzia il corso e ottieni l'oggetto CourseType
        v_course_obj := DEREF(v_course);

        -- Ottiene il DegreeProgram del corso
        v_degree_program := v_course_obj.DegreeProgram;

        -- Confronta il DegreeProgram del corso con il DegreeProgram in input
        IF v_degree_program != DegreeProgramRef THEN
            RETURN 'f';  -- Se non corrispondono, restituisce 'f'
        END IF;
    END LOOP;

    -- Se tutti i corsi appartengono allo stesso DegreeProgram, restituisce 't'
    RETURN 't';

EXCEPTION
    WHEN OTHERS THEN
        RETURN 'f';  -- In caso di errore, restituisce 'f'
END;

