--------------------------------------------------------
--  DDL for Function CHECKPREREQUISITEFUNCTION
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "C##UNIDIDATTICA"."CHECKPREREQUISITEFUNCTION" (
    p_courses CourseArrayType,
    p_student REF StudentType
) RETURN CHAR
IS
    v_prerequisite REF CourseType;
    v_passed_exams NUMBER;
BEGIN
    -- Verifica se l'array di prerequisiti è vuoto
    IF p_courses.COUNT = 0 THEN
        RETURN 't'; -- Nessun prerequisito da verificare, ritorna 't'
    END IF;

    -- Ciclo attraverso i corsi del VARRAY
    FOR i IN 1..p_courses.COUNT LOOP
        v_prerequisite := p_courses(i);

        -- Controlla se lo studente è iscritto al corso e ha passato l'esame
        SELECT COUNT(*)
        INTO v_passed_exams
        FROM Enrollment en
        WHERE DEREF(en.Student).matriculation = DEREF(p_student).matriculation
        AND DEREF(en.Course).CourseCode = DEREF(v_prerequisite).CourseCode
        AND en.ExamPassed = 't';

        -- Se lo studente non ha passato l'esame del prerequisito, restituisce 'f'
        IF v_passed_exams = 0 THEN
            RETURN 'f';
        END IF;
    END LOOP;

    -- Se tutti i prerequisiti sono soddisfatti, restituisce 't'
    RETURN 't';
EXCEPTION
    WHEN OTHERS THEN
        RETURN 'f'; -- In caso di errore, restituisci 'f'
END;

