--  DDL for Function CREDITSOBTAINED
--------------------------------------------------------

  CREATE OR REPLACE EDITIONABLE FUNCTION "C##UNIDIDATTICA"."CREDITSOBTAINED" (Matr CHAR) RETURN NUMBER IS
    total_credits NUMBER := 0;
BEGIN
    -- Seleziona e somma i crediti per tutti i corsi con esami passati dallo studente specifico
    SELECT SUM(c.Credits)
    INTO total_credits
    FROM Enrollment e
    JOIN Course c ON e.Course = REF(c)  -- Collegamento con la tabella Course
    JOIN Student s ON e.Student = REF(s)  -- Collegamento con la tabella Student
    WHERE s.Matriculation = Matr  -- Filtra per la matricola
    AND e.ExamPassed = 't';  -- Filtra per esami passati

    RETURN NVL(total_credits, 0);  -- Restituisce 0 se nessun corso è stato trovato
END;

